import React, {Component, useCallback} from "react";
import { Button, TextField } from '@material-ui/core';
import axios from 'axios';
const axiosPOSTconfig = {headers: {'Content-Type': 'application/json'}};


class CreateKey extends Component{

    constructor(props) {
        super(props)
        this.state = {
            key: "",
            name: ""
        }
    }

    onChange = (e) => {
        this.setState({[e.target.id]: e.target.value});
    }

    MyDropzone() {
        const onDrop = useCallback(acceptedFiles => {
            // Do something with the files
        }, [])
        const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

        return (
            <div {...getRootProps()}>
                <input {...getInputProps()} />
                {
                    isDragActive ?
                        <p>Drop the files here ...</p> :
                        <p>Drag 'n' drop some files here, or click to select files</p>
                }
            </div>
        )
    }

    onSubmit = (event) => {
        let {key, name} = this.state;
        event.preventDefault();
        axios.post('http://localhost:8082/keys/create', JSON.stringify({
            'key': key,
            'name': name
        }), axiosPOSTconfig)
            .then((response) => {
                this.setState({status: response.data.status});
            })
            .catch((error) => {console.log(error)});
    }

    render() {
        let {key, name} = this.state;
        return(
            <main>
                <div>
                    <form onSubmit={this.onSubmit}>
                        <TextField id="key" type="text" value={key} placeholder={"Key"} onChange={this.onChange}/><br/>
                        <TextField id="name" type="text" value={name} placeholder={"Game name"} onChange={this.onChange}/><br/>
                        <Button onClick={this.onSubmit} variant="contained" color="primary">Create Game</Button>
                    </form>
                </div>
            </main>
        );
    }
}
export default CreateKey;
