import wReact, {Component, useState} from "react";
import axios from "axios";
import {useDropzone} from "react-dropzone";
import { DataGrid } from '@material-ui/data-grid';

const columns = [
    { field: "id", headerName: 'ID', width: 70 },
    { field: "date", headerName: 'Date', width: 70 },
    { field: "description", headerName: 'Description', width: 130 },
    { field: "game", headerName: 'Game', width: 130 },
    { field: "price", headerName: 'Price', width: 70 }
];
export class Games extends Component{

    constructor(props) {
        super(props);
        this.state = {
            rows: []
        };
    }

    componentDidMount() {
        axios.get('http://localhost:8082/games')
            .then((response) => {this.setState({rows: response.data.rows});})
            .catch((error) => {console.log(error); this.setState({ message: error.message })});
    }

    render() {
        return (
            <main role="main" class="container">
                <div>
                    {this.state.rows === null && <p>Loading menu...</p>}
                        <table>
                            <tr><th>ID</th><th>Date</th><th>Description</th><th>Game</th><th>Price</th></tr>
                            {this.state.rows && this.state.rows.map(game => (
                            <tr><td>{game.id}</td><td>{game.date}</td><td>{game.name}</td><td>{game.description}</td><td>{game.game}</td><td>{game.price}</td></tr>
                            ))
                            }
                        </table>
            </div>
        </main>
    );
}
}


export default Games;
