import React, {Component, useState} from "react";
import axios from "axios";
import {useDropzone} from "react-dropzone";
import { DataGrid } from '@material-ui/data-grid';

const columns = [
    { field: "game.id", headerName: 'ID', width: 70 },
    { field: "game.date", headerName: 'Date', width: 70 },
    { field: "game.description", headerName: 'Description', width: 130 },
    { field: "game.name", headerName: 'Game', width: 130 },
    { field: "game.price", headerName: 'Price', width: 70 }
];
export class Games extends Component{

    constructor(props) {
        super(props);
        this.state = {
            rows: []
        };
    }

    componentDidMount() {
        axios.get("http://localhost:8082/games")
            .then((response) => {this.setState({rows: response.data.rows});})
            .catch((error) => {console.log(error); this.setState({ message: error.message })});
    }

    render() {
        return (
            <main role="main" class="container">
                <div>
                    {this.state.rows === null && <p>Loading menu...</p>}
                    <DataGrid columns={columns} pageSize={5} checkboxSelection>
                    {this.state.rows && this.state.rows.map(game => (
                       this.state.rows
                    ))
                    }
                </DataGrid>
            </div>
        </main>
    );
}
}


export default Games;
