import React, {Component} from "react";
import { Button, TextField } from '@material-ui/core';
import axios from 'axios';
const axiosPOSTconfig = {headers: {'Content-Type': 'application/json'}};


class CreateGame extends Component{

    constructor(props) {
        super(props)
        this.state = {
            nameField: "",
            priceField: "",
            descriptionField: "",
            dateField: ""
        }
    }

    onChange = (e) => {
        this.setState({[e.target.id]: e.target.value});
    }

    onSubmit = (event) => {

        let {nameField, priceField, descriptionField, dateField} = this.state;
        event.preventDefault();
        axios.post('http://localhost:8082/games/create', JSON.stringify({
            'nameField': nameField,
            'priceField': priceField,
            'descriptionField': descriptionField,
            'dateField': dateField
        }), axiosPOSTconfig)
            .then((response) => {
            this.setState({status: response.data.status});
        })
            .catch((error) => {console.log(error)});
    }

    render() {
        let {nameField, priceField, descriptionField, dateField} = this.state;
        return(
            <main>
                <div>
                    <form onSubmit={this.onSubmit}>
                    <TextField id="nameField" type="text" value={nameField} placeholder={"Name"} onChange={this.onChange}/><br/>
                    <TextField id="priceField" type="text" value={priceField} placeholder={"Price"} onChange={this.onChange}/><br/>
                    <TextField id="descriptionField" type="text" value={descriptionField} placeholder={"Description"} onChange={this.onChange}/><br/>
                    <TextField id="dateField" type="text" value={dateField} placeholder={"Date"} onChange={this.onChange}/><br/>
                    <Button onClick={this.onSubmit} variant="contained" color="primary">Create Game</Button>
                    </form>
                </div>
            </main>
        );
    }
}

export default CreateGame;
