import React, {Component} from "react";
import { Button, TextField } from '@material-ui/core';
import axios from 'axios';
const axiosPOSTconfig = {headers: {'Content-Type': 'application/json'}};


class CreateGame extends Component{

    constructor(props) {
        super(props)
        this.state = {
            nameField: "",
            priceField: "",
            descriptionField: "",
            dateField: ""
        }
    }

    onChange = (e) => {
        this.setState({[e.target.id]: e.target.value});
    }

    onSubmit = (event) => {
/*
        let {nameField, priceField, descriptionField, dateField} = this.state;
*/
        event.preventDefault();
        axios.post('http://localhost:8082/games/create', JSON.stringify({
            'nameField': this.nameField,
            'priceField': this.priceField,
            'descriptionField': this.descriptionField,
            'dateField': this.dateField
        }), axiosPOSTconfig)
            .then((response) => {
            this.setState({status: response.data.status});
        })
            .catch((error) => {console.log(error)});
    }

    render() {
/*        let {nameField, priceField, descriptionField, dateField} = this.state;*/
        return(
            <main>
                <div>
                    <form onSubmit={this.onSubmit}>
                    <TextField id={this.nameField} type="text" value={this.nameField} placeholder={"Name"} onChange={this.onChange}/><br/>
                    <TextField id={this.priceField} type="text" value={this.priceField} placeholder={"Price"} onChange={this.onChange}/><br/>
                    <TextField id={this.descriptionField} type="text" value={this.descriptionField} placeholder={"Description"} onChange={this.onChange}/><br/>
                    <TextField id={this.dateField} type="text" value={this.dateField} placeholder={"Date"} onChange={this.onChange}/><br/>
                    <Button onClick={this.onSubmit} variant="contained" color="primary">Create Game</Button>
                    </form>
                </div>
            </main>
        );
    }
}

export default CreateGame;
