import React, {Component} from "react";
import { Button, TextField } from '@material-ui/core';
import axios from 'axios';

export default class CreateGame extends Component{
    constructor(props) {
        super(props)
        this.state = {
            nameField: "",
            priceField: "",
            descriptionField: "",
            dateField: ""
        }
        this.createGame = this.createGame.bind(this);
    }

    onChange = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }

    createGame = (event) => {

        const formData = new FormData();
        formData.append("file", file);
        axios.post(`http://localhost:8082/games/${id}/mainImage/upload`,
            formData,

        ).then(() => {
            console.log("file uploaded successfully")
        }).catch(err => {
            console.log(err);
        });
        e.preventDefault();
        let {name, price, description, date} = this.state;
        axios.post('http://localhost:8082/games/create', JSON.stringify({
            'name': this.nameField,
            'price': this.priceField,
            'description': this.descriptionField,
            'date': this.dateField
        }),             {
            headers: {
                "Content-Type": "multipart/form-data"
            }
        }).then((response) => {
            this.setState({status: response.data.status});
        })
            .catch((error) => {console.log(error)});
    }

    render() {

        const inputProps = {
            step: 300,
        };
        return(
            <div>
                <form onSubmit={this.createGame}>
                    <TextField name={this.nameField} type="text" inputProps={inputProps} placeholder={"Name"} onChange={this.onChange}/><br/>
                    <TextField name={this.priceField} type="text" inputProps={inputProps} placeholder={"Price"} onChange={this.onChange}/><br/>
                    <TextField name={this.descriptionField} type="text" inputProps={inputProps} placeholder={"Description"} onChange={this.onChange}/><br/>
                    <TextField name={this.dateField} type="text" inputProps={inputProps} placeholder={"Date"} onChange={this.onChange}/><br/>
                    <Button onClick={this.createGame} variant="contained" color="primary">Create Game</Button>
                </form>
            </div>
        );
    }
}
