import React, {Component} from "react";
import { Button, Input } from '@material-ui/core';
import axios from 'axios';

class CreateGame extends Component{

    constructor(props) {
        super(props)
        this.state = {
            nameField: "",
            priceField: "",
            descriptionField: "",
            dateField: ""
        }
        this.createGame = this.createGame.bind(this);
    }

    onChange = (e) => {
        this.setState({[e.target.id]: e.target.value});
    }

    createGame = (event) => {
        let {nameField, priceField, descriptionField, dateField} = this.state;
        event.preventDefault();
        axios.post('http://localhost:8082/games/create', JSON.stringify({
            'nameField': nameField,
            'priceField': priceField,
            'descriptionField': descriptionField,
            'dateField': dateField
        }),             {
            headers: {
                "Content-Type": "multipart/form-data"
            }
        }).then((response) => {
            this.setState({status: response.data.status});
        })
            .catch((error) => {console.log(error)});
    }

    render() {
        let {nameField, priceField, descriptionField, dateField} = this.state;
        const inputProps = {
            step: 300,
        };
        return(
            <div>
                    <Input id="nameField" type="text" inputProps={inputProps} placeholder={"Name"} onChange={this.onChange}/><br/>
                    <Input id="priceField" type="text" inputProps={inputProps} placeholder={"Price"} onChange={this.onChange}/><br/>
                    <Input id="descriptionField" type="text" inputProps={inputProps} placeholder={"Description"} onChange={this.onChange}/><br/>
                    <Input id="dateField type" type="text" inputProps={inputProps} placeholder={"Date"} onChange={this.onChange}/><br/>
                    <Button onClick={this.createGame} variant="contained" color="primary">Create Game</Button>
            </div>
        );
    }
}

export default CreateGame;
