import React, {Component} from "react";
import axios from "axios";
import { DataGrid } from '@material-ui/data-grid';
import {Link} from "react-router-dom";
import {Button} from "@material-ui/core";

const columns = [
    { field: "id", headerName: 'ID', width: 70 },
    { field: "date", headerName: 'Date', width: 70 },
    { field: "description", headerName: 'Description', width: 130 },
    { field: "name", headerName: 'Game', width: 130 },
    { field: "price", headerName: 'Price', width: 70 }
];

export class Games extends Component{
    constructor(props) {
        super(props);
        this.state = {
            rows: []
        };
    }

    componentDidMount() {
        axios.get('http://localhost:8082/games')
            .then((response) => {this.setState({rows: response.data.rows});})
            .catch((error) => {console.log(error); this.setState({ message: error.message })});
    }

    render() {
        let {id, data, description, name, price, image} = this.date.rows;
        return (
            <main role="main" className="container">
                <div className="jumbotron">
                    <div>
                        <Button component={Link} to="/CreateGame">Add Game</Button>
                    </div>
                </div>
                <div>
                    {this.state.rows === null && <p>Loading menu...</p>}
                    <table>
                        <thead>
                            <tr><th>id</th><th>date</th><th>description</th><th>name</th><th>price</th><th>imageLink</th></tr>
                        </thead>
                        {this.state.rows && this.state.rows.map(game => (
                            <tbody>
                                <tr><td>{id}</td><td>{date}</td><td>{description}</td><td>{name}</td><td>{price}</td><td>{image}</td></tr>
                            </tbody>
                            ))
                        }
                    </table>
{/*                    {this.state.rows && this.state.rows.map(game => (
                        <DataGrid columns={columns} rows={game} pageSize={5} checkboxSelection />
                    ))
                    }*/}
                </div>
            </main>
        );
    }
}
