import React, {Component} from "react";
import { Button, TextField } from '@material-ui/core';
import axios from 'axios';

export default class CreateGame extends Component{
    constructor(props) {
        super(props)
        this.state = {
            nameField: "",
            priceField: "",
            descriptionField: "",
            dateField: ""
        }
        this.createGame = this.createGame.bind(this);
    }

    createGame = (event) => {
        /*        `,
                    formData,
                    {
                        headers: {
                            "Content-Type": "multipart/form-data"
                        }
                    }
                ).then(() => {
                    console.log("file uploaded successfully")
                }).catch(err => {
                    console.log(err);
                });*/
        this.setState({
            nameField: event.target.value,
            priceField: event.target.value,
            descriptionField: event.target.value,
            dateField: event.target.value
        })
        axios.post('http://localhost:8082/games/create', JSON.stringify({
            'name': this.nameField,
            'price': this.priceField,
            'description': this.descriptionField,
            'date': this.dateField
        })).then((response) => {
            this.setState({status: response.data.status});
        })
            .catch((error) => {console.log(error)});
    }

    render() {
        const inputProps = {
            step: 300,
        };
        return(
            <div>
                <form createGame={this.createGame}>
                    <TextField id={this.nameField} type="" inputProps={inputProps} placeholder={"Name"}/>
                    <p/>
                    <TextField id={this.priceField} type="" inputProps={inputProps} placeholder={"Price"}/>
                    <p/>
                    <TextField id={this.descriptionField} type="" inputProps={inputProps} placeholder={"Description"}/>
                    <p/>
                    <TextField id={this.dateField} type="" inputProps={inputProps} placeholder={"Date"}/>
                    <p/>
                    <Button onClick={this.createGame} variant="contained" color="primary">Create Game</Button>
                </form>
            </div>
        );
    }
}
